mapper.update:
UPDATE user
SET password = #{hashedPwd}
WHERE id = #{id}

update user
set role = #{role},
is_disabled = #{isDisabled},
update_by = #{updateBy}
where id = #{userId}

mapper.select:
Select id, username, role, is_disabled
FROM user


    username like "%"#{username}"%"


    and role = #{role}


    and is_disabled = #{isDisabled}

select username
from user
where id = #{id}
