mapper.update:
UPDATE user
SET password = #{hashedPwd}
WHERE id = #{id}

update user


    role = #{role},


    is_disabled = #{isDisabled},


    update_by = #{updateBy}


where id = #{id}

mapper.select:
Select id, username, role, is_disabled, create_by, create_time, update_by, update_time
FROM user


    username like "%"#{username}"%"


    and role = #{role}


    and is_disabled = #{isDisabled}

select username
from user
where id = #{id}
