mapper.update:
UPDATE user
SET password = #{hashedPwd}
WHERE id = #{id}

update user


    role = #{role},


    is_disabled = #{isDisabled},


    update_by = #{updateBy},

  update_time = #{updateTime}

where id = #{id}

mapper.delete:
insert into deleted_user (user_id, username, password, salt, role, create_time, create_by, delete_time, deleted_by)
select id as 'user_id', username, password, salt, role, create_time, create_by, now(), #{deletedBy}
from user
where id = #{id}

mapper.select:
Select id, username, role, is_disabled, create_by, create_time, update_by, update_time
FROM user


    username like "%"#{u.username}"%"


    and role = #{u.role}


    and is_disabled = #{u.isDisabled}

select username
from user
where id = #{id}
