mapper.update:
UPDATE user
SET password = #{hashedPwd}
WHERE id = #{id}
and is_del = 0

mapper.select:
Select id, username, role, is_disabled, create_by, create_time, update_by, update_time, is_del FROM user
username like "%"#{u.username}"%"
and role = #{u.role}
and is_disabled = #{u.isDisabled}
and is_del = 0 and type = 'USER'
